// Load the data of the above array, expand it into RGBA data
uint8_t* LoadRGBA (bool bBlack)
{
    // RGBA structure
    typedef struct {
        uint8_t r,g,b,a;
    } RGBAty;
    
    // Black & White
    const RGBAty BLACK = { 0x06, 0x08, 0x0b, 0xff };
    const RGBAty WHITE = { 0xff, 0xff, 0xff, 0xff };
    const RGBAty& col = bBlack ? BLACK : WHITE;     // which color to use for visible pixels?

    // allocate the necessary memory: 4 bytes (RGBA) per pixel
    RGBAty* pRGBA = new RGBAty[IMG_SIZE];
    if (!pRGBA) return nullptr;
    RGBAty* p = pRGBA;
    
    // Loop the RLE alpha data
    for (uint16_t rle: ALPHA_RLE)
    {
        uint8_t n = uint8_t(rle >> 8);              // upper byte is the number of pixel to fill
        uint8_t a = uint8_t(rle & 0x00ff);          // lower byte is the alpha value
        
        // A visible pixel uses color `col`, but by convention an invisible pixel (alpha = 0) always uses color WHITE
        RGBAty pixel = a > 0 ? col : WHITE;
        pixel.a = a;                                // use the Alpha value from the RLE array
        
        // add as many pixels as stated by the run-length
        for (;n > 0; --n)
            *(p++) = pixel;
    }
    
    // when we are done we expect to have _exactly_ filled the image
    assert(p - pRGBA == IMG_SIZE);
    
    // return the loaded data
    return (uint8_t*)pRGBA;
}

// The one public function
bool GetTexture (int& id, bool bBlack)
{
    static bool doneBlack = false, doneWhite = false;   // has texture been created already?
    static int idBlack = 0, idWhite = 0;                // texture id if already created
    
    // Already created the wanted id?
    if (bBlack && doneBlack)    { id = idBlack; return true; }
    if (!bBlack && doneWhite)   { id = idWhite; return true; }
    
    // Load the logo data as RGBA data into memory
    uint8_t* pRGBA = LoadRGBA(bBlack);
    if (!pRGBA) return false;
    
    // Load the logo as an OpenGL texture
    int &texId = bBlack ? idBlack : idWhite;
    XPLMGenerateTextureNumbers(&texId, 1);
    XPLMBindTexture2d(texId, 0);

// Apple has deprecated OpenGL...but let's not get talkative about it
#if defined(__clang__)
#pragma clang diagnostic push
#pragma clang diagnostic ignored "-Wdeprecated-declarations"
#endif
    glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MIN_FILTER, GL_LINEAR);
    glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MAG_FILTER, GL_LINEAR);
    glPixelStorei(GL_UNPACK_ROW_LENGTH, 0);
    glTexImage2D(GL_TEXTURE_2D, 0,
                 GL_RGBA, IMG_WIDTH, IMG_HEIGHT, 0,
                 GL_RGBA, GL_UNSIGNED_BYTE, pRGBA);
#if defined(__clang__)
#pragma clang diagnostic pop
#endif
    // free our own RGBA data
    delete[] pRGBA;
    
    // return the texture id to the caller
    if (bBlack) doneBlack = true;
    else doneWhite = true;
    id = texId;
    return true;
}

